bool bad_char(char c)
{
    switch (c)
    {
    case ',':
        return true;
    default:
        return false;
    }
}

int countup(std::string rego)
{
    int count = 0;
    int last = 0;
    char buffer[4]{};
    for (std::string::size_type i = 0; i < rego.size(); ++i)
    {
        char c = rego[i];
        if (c == ' ')
        {
            count++;
            last = 0;
        }
        else
            last++;
    }

    return count;
}

void convert()
{
    // Converting to byte array
    byte_data.erase(std::remove_if(byte_data.begin(), byte_data.end(), &bad_char), byte_data.end());
    byte_data += ' '; // Add 1 space because we grab last 4 symbols before spaces, if not this, the last byte won't be grabbed

    // Fill array with empty bytes
    int byte_count = countup(byte_data);
    uint8_t* nw = new uint8_t[byte_count];
    auto dynsize = sizeof(nw) * byte_count / sizeof(uint8_t*);

    for (int i = 0; i < dynsize; i++)
        nw[i] = g_binary[i];

    delete[] g_binary;
    g_binary = nw;
    nw = NULL;

    int last = 0;
    char buffer[4]{};
    std::vector<uint8_t> bytes;
    for (std::string::size_type i = 0; i < byte_data.size(); ++i)
    {
        char c = byte_data[i];
        if (c == ' ')
        {
            // Unite all 4 chars together
            std::string temp{};
            for (char& s : buffer)
                temp += s;

            // Convert the thing - (std::string)"0x53" -> (long)83
            char* p;
            long n = strtol(temp.c_str(), &p, 16);
            uint8_t val = (uint8_t)n;
            bytes.push_back(val);

            last = 0;
        }
        else
        {
            buffer[last] = c;
            last++;
        }
    }

    std::copy(bytes.begin(), bytes.end(), g_binary);
}
